const progressListener = (offlineRegion, status) => console.log(offlineRegion, status);
const errorListener = (offlineRegion, error) => console.log(offlineRegion, error);

const offlinePack = await OfflineManager.createPack({
  mapStyle: 'https://demotiles.maplibre.org/tiles/tiles.json',
  minZoom: 14,
  maxZoom: 20,
  bounds: [west, south, east, north],
  metadata: { customValue: 'myValue' }
}, progressListener, errorListener)

const progressListener = (offlinePack, status) => console.log(offlinePack, status)
const errorListener = (offlinePack, error) => console.log(offlinePack, error)
OfflineManager.addListener(pack.id, progressListener, errorListener)

// creates a temp file png of base map
const uri = await SnapshotManager.takeSnap({
  centerCoordinate: [-74.126410, 40.797968],
  width: width,
  height: height,
  zoomLevel: 12,
  pitch: 30,
  heading: 20,
  styleURL: StyleURL.Default,
  writeToDisk: true, // Create a temporary file
});

// creates base64 png of base map without logo
const uri = await SnapshotManager.takeSnap({
  centerCoordinate: [-74.126410, 40.797968],
  width: width,
  height: height,
  zoomLevel: 12,
  pitch: 30,
  heading: 20,
  styleURL: StyleURL.Default,
  withLogo: false, // Disable Mapbox logo (Android only)
});

// creates snapshot with bounds
const uri = await SnapshotManager.takeSnap({
  bounds: [[-74.126410, 40.797968], [-74.143727, 40.772177]],
  width: width,
  height: height,
  styleURL: StyleURL.Default,
});

